mapper.select:
SELECT * FROM tb_buy


        uptype = #{selectName}


        and buytitle like '%${searchName}%'


        and department = #{department}


        and buyerid = #{buyerid}


        and itemtype = #{itemtype}


        and itemid = #{itemid}


        and btime = #{btime}


order by buyid

    ,importance


    ,arrivaltime


    ,btime


    desc

mapper.update:
update tb_buy
set buytitle=#{buytitle}
  , btime=#{btime}
  , itemtype=#{itemtype}
  , itemid=#{itemid}
  , num=#{num}
  , unit=#{unit}
  , importance=#{importance}
  , comment=#{comment}
  , arrivaltime=#{arrivaltime}
  ,department=#{department}
  , importance=#{importance}
  , buytype=#{buytype}
where buyid = #{buyid};

update tb_buy
set uptype=#{uptype}
where buyid = #{buyid};
