mapper.select:
SELECT * FROM tb_need


        needtitle like '%${searchName}%'


        and uptype = #{selectName}

mapper.insert:
insert into tb_need(needid, needtitle, itemtype, itemid, neednum, unit, needday, neederid, comment, uptype)
values (null, #{needtitle}, #{itemtype}, #{itemid}, #{neednum}, #{unit}, #{needday}, #{neederid},
        #{comment}, 0);

mapper.update:
update tb_need
set needtitle=#{needtitle}
  , itemtype=#{itemtype}
  , itemid=#{itemid}
  , neednum=#{neednum}
  , unit=#{unit}
  , needday=#{needday}
  , neederid=#{neederid}
  , comment=#{comment}
where needid = #{needid};

update tb_need
set uptype=#{uptype}
where needid = #{needid};
